import React, { useState, useEffect, useRef } from "react";
import Lines from "./Line";
import useWebSocket from 'react-use-websocket';
import throttle from 'lodash.throttle';
import { Cursor } from "./Components/Cursor";

const renderCursors = users => {
    return Object.keys(users).map(uuid => {
        const user = users[uuid];

        return (
            <Cursor key={uuid} point={[user.state.x, user.state.y]} />
        );
    });
};

const renderMachines = (groups, machines, handleDragEnter, handleDragStart) => {
    return (
        <div className="drag-n-drop">
            {groups.map(group => (
                <div
                    onDragEnter={(e) => handleDragEnter(e, group)}
                    key={group}
                    className="dnd-group"
                >
                    <div className='group-title'>{group}</div>
                    {machines.filter(machine => machine.group === group).map(item => (
                        <div
                            key={item.id}
                            id={item.id}
                            className="dnd-item"
                            draggable
                            onDragStart={(e) => handleDragStart(e)}
                        >
                            <p>{item.value}</p>
                            <Lines group={item.group} />
                        </div>
                    ))}
                </div>
            ))}
        </div>
    );
};

function DragNDrop({ machines: initialMachines, groups: initialGroups, username }) {
    const WS_URL = 'ws://localhost:8000';
    const { sendJsonMessage, lastJsonMessage } = useWebSocket(WS_URL, {
        queryParams: { username }
    });

    const THROTTLE = 50;
    const sendJsonMessageThrottle = useRef(throttle(sendJsonMessage, THROTTLE));

    const [users, setUsers] = useState({});

    useEffect(() => {
        sendJsonMessage({
            x: 0,
            y: 0,
            machines: initialMachines,
            groups: initialGroups
        });
        window.addEventListener("mousemove", e => {
            sendJsonMessageThrottle.current({
                x: e.clientX,
                y: e.clientY
            });
        });
    }, [initialMachines, initialGroups, sendJsonMessageThrottle]);

    useEffect(() => {
        if (lastJsonMessage) {
            setUsers(lastJsonMessage);
        }
    }, [lastJsonMessage]);

    const [list, setList] = useState(initialMachines);
    const [dragging, setDragging] = useState(false);

    const handleDragStart = (e) => {
        e.target.addEventListener('dragend', handleDragEnd(e));
        setTimeout(() => {
            setDragging(e.target);
        }, 0);
    };

    const handleDragEnter = (e, group) => {
        setList(prevList => {
            const updatedList = [...prevList];
            updatedList[dragging.id - 1].group = group;
            return updatedList;
        });
    };

    const handleDragEnd = (e) => {
        setDragging(false);
        e.target.removeEventListener('dragend', handleDragEnd);
    };

    let groups = initialGroups;
    let machines = initialMachines;

    // Extract groups and machines from all users
    if (Object.keys(users).length > 0) {
        groups = [...new Set(Object.values(users).flatMap(user => user.state.groups))];
        machines = Object.values(users).flatMap(user => user.state.machines);
    }

    return (
        <>
            {Object.keys(users).length > 0 && renderCursors(users)}
            {renderMachines(groups, machines, handleDragEnter, handleDragStart)}
        </>
    );
}

export default DragNDrop;
