import React, {useRef, useEffect, useState} from "react";

function Lines({group, onSelect }) {
  const larges = [0];
  const cells = [0];
  let listRef = useRef();
  const [selectedItem, setSelectedItem] = useState(null);

  const [open, setOpen] = useState(false);
  useEffect(() => {

    console.log(selectedItem);
    let handler = (e) => {
      if (listRef.current){
        if(!listRef.current.contains(e.target)){
          setSelectedItem(null);
        } else if (e.target.className === 'anchor') {
          setSelectedItem(null)
        }
      }
    }; 

    document.addEventListener("mousedown", handler);

    return() => {
      document.removeEventListener("mousedown", handler);
    }
    
  }, []); 

  const queueTypes = [
    { level: "Priority", type: "priority" },
    { level: "Reorder", type: "reorder" },
    { level: "Backstock", type: "backstock" },
  ];


  const handleClick = (e) => {
    setSelectedItem(e.target.parentElement);
    console.log(e.target);
  }

  

  const handleSelect = (e, level) => {
    const target = e.target.parentElement;
    const value = e.target.value;
    if (target.classList.contains("selected")) {
      target.classList.remove("selected");
    } else {
      target.classList.add("selected");
    }
    onSelect(level, e.target.checked, value);
  };

  if (group === "Larges") {
    for (let i = 0; i < 7; i++) {
      larges.push(
        <li key={i}>
          <input
            type="checkbox"
            value={i}
            onClick={(e) => handleSelect(e, "Larges")}
          />
          {i + 1}
        </li>
      );
    }

    return (
      <div className="container" ref={listRef}>
        {queueTypes.map((queue) => (
          <div id={queue.type} className="list" key={queue.type} >
          <div onClick={(e) => {handleClick(e)}} className={`dropdown ${selectedItem ? 'visible' : 'hidden'}`}>
              <span className="anchor">{queue.level}</span>
              <ul className="lines">
                {larges.map((item, index) => (
                  <li key={index}>
                    <input
                      type="checkbox"
                      value={index}
                      onClick={(e) => handleSelect(e, queue.level)}
                    />
                    {index === 0 ? "All" : index}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </div>
    );
  }

  if (group === "Cells") {
    for (let i = 0; i < 14; i++) {
      cells.push(
        <li key={i}>
          <input
            type="checkbox"
            value={i}
            onClick={(e) => handleSelect(e, "Cells")}
          />
          {i === 0 ? "All" : i}
        </li>
      );
    }

    return (
      <div  className="container" ref={listRef}>
        {queueTypes.map((queue) => (
          <div id={queue.type} className="list" key={queue.type} >
          <div onClick={(e) => {handleClick(e)}} className={`dropdown ${open ? 'visible' : 'hidden'}`}>
              <span className="anchor">{queue.level}</span>
              <ul className="lines">
                {cells.map((item, index) => (
                  <li key={index}>
                    <input
                      type="checkbox"
                      value={index}
                      onClick={(e) => handleSelect(e, queue.level)}
                    />
                    {index === 0 ? "All" : index}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </div>
    );
  }
}

export default Lines;
