import React, {useState, useEffect, useRef} from "react";
import Lines from "./Line";
import useWebSocket from 'react-use-websocket';
import throttle from 'lodash.throttle';

function DragNDrop ({machines, groups, username}) {
    const WS_URL = 'localhost:8000'
    const {sendJsonMessage } = useWebSocket(WS_URL, {
        queryParams: {username}
    });

    const THROTTLE = 50
    const sendJsonMessageThrottle = useRef(throttle(sendJsonMessage, THROTTLE))

    useEffect(() => {
        window.addEventListener("mousemove", e => {
           
            sendJsonMessageThrottle.current({
                x: e.clientX,
                y: e.clientY
            })

        })
    }, [])


    const [list, setList] = useState(machines);
    const [dragging, setDragging] = useState(false);

    const handleDragStart = (e) => {
        e.target.addEventListener('dragend', handleDragEnd(e));
        setTimeout(() => {
            setDragging(e.target)
        }, 0);
    };

    const handleDragEnter = (e, group) => {
        setList([...list, (list[dragging.id - 1].group = group)])
        }

    const handleDragEnd = (e) => {
        setDragging(false);
        e.target.removeEventListener('dragend', handleDragEnd);
        
    }

    return (
        <div className="drag-n-drop">
    {groups.map((group) => (
      <div 
        onDragEnter={(e) => handleDragEnter(e, group)}
        key={group} 
        className='dnd-group'
      >
      <div className='group-title'>{group}</div>
      {machines.filter((machine) => machine.group === group).map((item) => (
        <div
        key={item.id}
        id={item.id}
        className="dnd-item"
        draggable
        onDragStart={(e) => handleDragStart(e)}
        >
            <p>{item.value}</p>
            <Lines group={item.group} />
        </div>
      ))

      }
      </div>
    ))}
</div>
    )

}

export default DragNDrop
