import React from "react";

function Lines({ group, onSelect }) {
  const larges = ["All"];
  const cells = ["All"];

  const queueTypes = [
    { level: "Priority", type: "priority" },
    { level: "Reorder", type: "reorder" },
    { level: "Backstock", type: "backstock" },
  ];

  const displayList = (e) => {
    const target = e.target.parentElement;
    if (target.classList.contains("dropdown")) {
      if (target.classList.contains("visible")) target.classList.remove("visible");
      else target.classList.add("visible");
    }
  };

  const handleSelect = (e, level) => {
    const target = e.target.parentElement;
    const value = e.target.value;
    if (target.classList.contains("selected")) {
      target.classList.remove("selected");
    } else {
      target.classList.add("selected");
    }
    onSelect(level, e.target.checked, value);
  };

  if (group === "Larges") {
    for (let i = 0; i < 7; i++) {
      larges.push(
        <li key={i}>
          <input
            type="checkbox"
            value={i}
            onClick={(e) => handleSelect(e, "Larges")}
          />
          {i + 1}
        </li>
      );
    }

    return (
      <div className="container">
        {queueTypes.map((queue) => (
          <div id={queue.type} className="list" key={queue.type}>
            <div onClick={displayList} className="dropdown">
              <span className="anchor">{queue.level}</span>
              <ul className="lines">
                {larges.map((item, index) => (
                  <li key={index}>
                    <input
                      type="checkbox"
                      value={index}
                      onClick={(e) => handleSelect(e, queue.level)}
                    />
                    {index === 0 ? "All" : index}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </div>
    );
  }

  if (group === "Cells") {
    for (let i = 0; i < 14; i++) {
      cells.push(
        <li key={i}>
          <input
            type="checkbox"
            value={i + 1}
            onClick={(e) => handleSelect(e, "Cells")}
          />
          {i + 1}
        </li>
      );
    }

    return (
      <div className="container">
        {queueTypes.map((queue) => (
          <div id={queue.type} className="list" key={queue.type}>
            <div onClick={displayList} className="dropdown">
              <span className="anchor">{queue.level}</span>
              <ul className="lines">
                {cells.map((item, index) => (
                  <li key={index}>
                    <input
                      type="checkbox"
                      value={index + 1}
                      onClick={(e) => handleSelect(e, queue.level)}
                    />
                    {index + 1}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </div>
    );
  }
}

export default Lines;
