import React, { useState, useEffect } from "react";
import Lines from "./Line";
import PrintLines from "./PrintLines";
import useWebSocket from "react-use-websocket";
import throttle from "lodash.throttle";
import socketIO from "socket.io-client";
import { DragDropContext, Droppable, Draggable } from "@hello-pangea/dnd";

const socket = socketIO.connect("http://localhost:5000");

function Machines() {
  const [machines, setMachines] = useState({});
  const [selectedLines, setSelectedLines] = useState([]);

  useEffect(() => {
    const getData = async () => {
      try {
        const data = await fetch("http://localhost:5000/api");
        const json = await data.json();
        setMachines(json);
      } catch (error) {
        console.log(error);
      }
    };
    getData();
  }, []);

  useEffect(() => {
    socket.on("machines", (data) => {
      setMachines(data);
    });
  }, [socket]);

  function handleDragEnd({ destination, source }) {
    if (!destination) return;
    if (destination.index === source.index && destination.droppableId === source.droppableId) return;

    // TODO;

    socket.emit("taskDragged", {
      source,
      destination,
    });
  }

  const handleSelect = (lineType, level, isSelected) => {
    setSelectedLines((prevSelectedLines) => {
      if (isSelected) {
        return [...prevSelectedLines, { type: lineType, level }];
      } else {
        return prevSelectedLines.filter((line) => line.type !== lineType || line.level !== level);
      }
    });
    console.log(selectedLines[0]);
  };

  return (
    <div className="drag-n-drop">
      <DragDropContext onDragEnd={handleDragEnd}>
        {Object.entries(machines).map(([key, group]) => (
          <div key={key} className="dnd-group">
            <div className="group-title">{group.title}</div>

            <Droppable droppableId={group.title}>
              {(provided) => (
                <div ref={provided.innerRef} {...provided.droppableProps}>
                  {group.items.map((item, index) => (
                    <Draggable key={item.id} draggableId={`${item.id}`} index={index}>
                      {(provided) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className="dnd-item"
                        >
                          <p>{item.value}</p>
                          <Lines group={group.title} onSelect={handleSelect} />
                          <PrintLines selectedLines={selectedLines} />
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </div>
        ))}
      </DragDropContext>
    </div>
  );
}

export default Machines;
