import React, {useState, useRef} from "react";

function DragNDrop ({data, groups}) {
    const [list, setList] = useState(data);
    const [dragging, setDragging] = useState(false);

    // const dragItem = useRef();
    // const dragNode = useRef();

    const handleDragStart = (e) => {
        e.target.addEventListener('dragend', handleDragEnd(e));
        setTimeout(() => {
            setDragging(e.target)
        }, 0);
    };

    const handleDragEnter = (e, grpI) => {
        console.log(grpI)
        setList([...list, (list[dragging.id - 1].grpI = grpI)])
        }

    const handleDragEnd = (e) => {
        setDragging(false);
        e.target.removeEventListener('dragend', handleDragEnd);
        
    }

    // const getStyles = (e, params) => {
    //     const currentItem = e.target;
    //     if (currentItem.grpI === params.grpI && currentItem.itemI === params.itemI) {
    //         return 'current dnd-item'
    //     }
    //     return 'dnd-item'
        
    // }

    return (
        <div className="drag-n-drop">
    {data.map((grp, grpI) => (
      <div 
        onDragEnter={(e) => handleDragEnter(e, grpI)}
        key={grp.title} 
        className='dnd-group'
      >
      <div className='group-title'>{grp.title}</div>
        {grp.items.map((item, itemI) => (
        <div draggable 
            onDragStart={(e) => {handleDragStart(e, {grpI, itemI})}}
            key={item} 
            className="dnd-item"
            id={itemI}
            >
            {item}
          </div>
    ))}
      </div>
    ))}
</div>
    )

}

export default DragNDrop
