import React, { useState, useEffect, useRef } from "react";
import useWebSocket from 'react-use-websocket';
import Lines from "./Line";
import { Cursor } from "./Components/Cursor";
import throttle from 'lodash.throttle';

function DragNDrop({ machines, groups, username }) {
    const WS_URL = 'ws://localhost:8000';
    const { sendJsonMessage, lastJsonMessage } = useWebSocket(WS_URL, {
        queryParams: { username }
    });

    const THROTTLE = 50;
    const sendJsonMessageThrottle = useRef(throttle(sendJsonMessage, THROTTLE));

    const [list, setList] = useState(machines);
    const [dragging, setDragging] = useState(false);

    useEffect(() => {
        sendJsonMessage({
            x: 0,
            y: 0,
            machines,
            groups
        });

        const handleMouseMove = (e) => {
            sendJsonMessageThrottle.current({
                x: e.clientX,
                y: e.clientY,
                machines,
                groups
            });
        };

        window.addEventListener("mousemove", handleMouseMove);

        return () => {
            window.removeEventListener("mousemove", handleMouseMove);
        };
    }, [machines, groups, sendJsonMessageThrottle]);

    const handleDragStart = (e) => {
        setDragging(e.target);
    };

    const handleDragEnter = (e, group) => {
        // Update the group of the machine being dragged
        const machineId = parseInt(e.target.id);
        const updatedMachines = machines.map(machine => {
            if (machine.id === machineId) {
                return { ...machine, group };
            }
            return machine;
        });
        setList(updatedMachines);
    };

    const handleDragEnd = (e) => {
        setDragging(false);
    };

    if (lastJsonMessage) {
        return (
            <>
                {renderCursors(lastJsonMessage)}
                {renderMachines(lastJsonMessage, handleDragEnter, handleDragStart)}
            </>
        );
    }

    return (
        <div className="drag-n-drop">
            {groups.map(group => (
                <div
                    key={group}
                    className='dnd-group'
                    onDragEnter={(e) => handleDragEnter(e, group)}
                >
                    <div className='group-title'>{group}</div>
                    {machines.filter(machine => machine.group === group).map(item => (
                        <div
                            key={item.id}
                            id={item.id}
                            className="dnd-item"
                            draggable
                            onDragStart={(e) => handleDragStart(e)}
                        >
                            <p>{item.value}</p>
                            <Lines group={item.group} />
                        </div>
                    ))}
                </div>
            ))}
        </div>
    );
}

const renderCursors = (users) => {
    return Object.keys(users).map(uuid => (
        <Cursor key={uuid} point={[users[uuid].x, users[uuid].y]} />
    ));
};

const renderMachines = (users, handleDragEnter, handleDragStart) => {
    return (
        <>
            {Object.keys(users).map(uuid => (
                <div key={uuid} className="drag-n-drop">
                    {users[uuid].groups.map(group => (
                        <div
                            key={group}
                            className="dnd-group"
                            onDragEnter={(e) => handleDragEnter(e, group)}
                        >
                            <div className='group-title'>{group}</div>
                            {users[uuid].machines.filter(machine => machine.group === group).map(item => (
                                <div
                                    key={item.id}
                                    id={item.id}
                                    className="dnd-item"
                                    draggable
                                    onDragStart={(e) => handleDragStart(e)}
                                >
                                    <p>{item.value}</p>
                                    <Lines group={item.group} />
                                </div>
                            ))}
                        </div>
                    ))}
                </div>
            ))}
        </>
    );
};

export default DragNDrop;
