import React, { useState, useEffect } from "react";
import Lines from "./Lines";
import PrintLines from "./PrintLines";
import socketIO from "socket.io-client";
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';

const socket = socketIO.connect("http://localhost:5000");

function Machines() {
  const [machines, setMachines] = useState({});
  const [selectedItems, setSelectedItems] = useState({});

  useEffect(() => {
    const getData = async () => {
      try {
        const data = await fetch("http://localhost:5000/api");
        const json = await data.json();
        setMachines(json);
      } catch (error) {
        console.log(error);
      }
    };
    getData();
  }, []);

  useEffect(() => {
    socket.on("machines", (data) => {
      setMachines(data);
    });
  }, []);

  useEffect(() => {
    socket.on("selectedLinesUpdated", (data) => {
        setSelectedItems(data)
        console.log(data)
    });
  }, [])

  function handleDragEnd({ destination, source }) {
    if (!destination) return;
    if (destination.index === source.index && destination.droppableId === source.droppableId) return;

    // TODO: Handle drag and drop logic here

    socket.emit('taskDragged', {
      source,
      destination,
    });
  }

  const handleSelect = (machineId, level, isChecked, value) => {
    // setSelectedItems((prevItems) => {
    //   const newItems = { ...prevItems };
    //   if (!newItems[machineId]) {
    //     newItems[machineId] = [];
    //   }
    //   if (isChecked) {
    //     newItems[machineId] = [...newItems[machineId], { level, value: parseInt(value, 10) }];
    //   } else {
    //     newItems[machineId] = newItems[machineId].filter(
    //       (item) => item.value !== parseInt(value, 10) || item.level !== level
    //     );
    //   }
    //   return newItems;
    // });

    socket.emit('linesSelected', {
        machineId,
        level,
        isChecked,
        value
      })
  };

  return (
    <div className="drag-n-drop">
      <DragDropContext onDragEnd={handleDragEnd}>
        {Object.entries(machines).map(([key, group]) => (
          <div key={key} className="dnd-group">
            <div className='group-title'>{group.title}</div>
            <Droppable droppableId={group.title}>
              {(provided) => (
                <div ref={provided.innerRef} {...provided.droppableProps}>
                  {group.items.map((item, index) => (
                    <Draggable key={item.id} draggableId={`${item.id}`} index={index}>
                      {(provided) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className="dnd-item"
                        >
                          <p>{item.value}</p>
                          <Lines group={group.title} onSelect={(level, isChecked, value) => handleSelect(item.id, level, isChecked, value)} />
                          <PrintLines selectedLines={selectedItems[item.id] || []} />
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </div>
        ))}
      </DragDropContext>
    </div>
  );
}

export default Machines;
