import React, {useState} from "react";


function Lines ({group}) {

    const [open, setOpen] = useState(false)

    var larges = [];
    var cells = [];

    const queueTypes = [
        {level: "Priority", type: "priority"},
        {level: "Reorder", type: "reorder"},
        {level: "Backstock", type: "backstock"}
    ];

const displayList = (e) => {
    console.log(e.target)
    // const target = e.target; 
    // target.getElementsByClassName('anchor')[0].onclick = function(evt) {
    //     if (target.classList.contains('visible'))
    //       target.classList.remove('visible');
    //     else
    //       target.classList.add('visible');
    //   }
}


const handleOpen = () => {
    setOpen(!open)
}

    if (group === "Larges") {
        for (let i = 0; i < 7; i++) {
            larges.push(<li key={i}><input type="checkbox" />{i + 1}</li>)
        }

        return (
        <div>
            <ul className="lines">{larges}</ul>
        </div>
        )
    }

    if (group === "Cells") {
        for (let i=0; i < 14; i++ ) {
            cells.push(<li key={i}><input type="checkbox" />{i + 1}</li>)
        }

        return ( 
        <div className="container">
        {queueTypes.map((queue)=> (
            <div className={queue.type} key={queue.type}>
                <div className="dropdown">
                <span onClick={handleOpen} className="anchor">{queue.level}</span>
                {open ? (
                    <ul className="lines">{cells}</ul>
                ) : null}
                
                </div>
                
            </div>
            
        ))}
        </div>)
    }

};

export default Lines
