import React from "react";

function Lines ({group}) {

    var larges = [];
    var cells = [];

    const queueTypes = [
        {level: "Priority", type: "priority"},
        {level: "Reorder", type: "reorder"},
        {level: "Backstock", type: "backstock"}
    ];

const displayList = (e) => {
    const target = e.target.parentElement; 
   if(target.classList.contains('dropdown')) {
    if (target.classList.contains('visible'))
        target.classList.remove('visible');
      else
        target.classList.add('visible');
   }   
}

const handleSelect = (e) => {
    const target = e.target.parentElement; 
    if (target.classList.contains('selected')) {
        target.classList.remove('selected');
    } else {
        target.classList.add('selected')
    }
}


    if (group === "Larges") {
        for (let i = 0; i < 7; i++) {
            larges.push(<li key={i}><input type="checkbox" />{i + 1}</li>)
        }

        return (
            <div className="container">
            {queueTypes.map((queue)=> (
                <div className={queue.type} key={queue.type}>
                    <div onClick={displayList} className="dropdown">
                    <span className="anchor">{queue.level}</span>
                    <ul className="lines">{larges}</ul>
                    </div>
                </div>
                
            ))}
            </div>
        )
    }

    if (group === "Cells") {
        for (let i=0; i < 14; i++ ) {
            cells.push(<li onClick={handleSelect} key={i}><input type="checkbox" />{i + 1}</li>)
        }

        return ( 
        <div className="container">
        {queueTypes.map((queue)=> (
            <div id={queue.type} className="list" key={queue.type}>
                <div onClick={displayList} className="dropdown">
                <span className="anchor">{queue.level}</span>
                <ul className="lines">{cells}</ul>
                </div>
            </div>
            
        ))}
        </div>)
    }

};

export default Lines
