import React, { useState, useEffect, useRef } from "react";
import Lines from "./Line";
import useWebSocket from 'react-use-websocket';
import throttle from 'lodash.throttle';
import { Cursor } from "./Components/Cursor";

const renderCursors = users => {
    if (!users) return null;
    return Object.keys(users).map(uuid => {
        const user = users[uuid];
        return <Cursor key={uuid} point={[user.state.x, user.state.y]} />;
    });
};

const renderMachines = (users, handleDragEnter, handleDragStart) => {
    if (!users) return null;
    return (
        <>
            {Object.keys(users).map(uuid => {
                const user = users[uuid];
                return (
                    <div key={uuid} className="drag-n-drop">
                        {user.state.groups.map(group => (
                            <div
                                onDragEnter={e => handleDragEnter(e, group)}
                                key={group}
                                className='dnd-group'
                            >
                                <div className='group-title'>{group}</div>
                                {user.state.machines.filter(machine => machine.group === group).map(item => (
                                    <div
                                        key={item.id}
                                        id={item.id}
                                        className="dnd-item"
                                        draggable
                                        onDragStart={e => handleDragStart(e)}
                                    >
                                        <p>{item.value}</p>
                                        <Lines group={item.group} />
                                    </div>
                                ))}
                            </div>
                        ))}
                    </div>
                );
            })}
        </>
    );
};

function DragNDrop({ machines, groups, username }) {
    const WS_URL = 'ws://localhost:8000';
    const { sendJsonMessage, lastJsonMessage } = useWebSocket(WS_URL, {
        queryParams: { username }
    });

    const THROTTLE = 50;
    const sendJsonMessageThrottle = useRef(throttle(sendJsonMessage, THROTTLE));

    useEffect(() => {
        sendJsonMessage({
            x: 0,
            y: 0,
            machines,
            groups
        });

        const handleMouseMove = e => {
            sendJsonMessageThrottle.current({
                x: e.clientX,
                y: e.clientY
            });
        };

        window.addEventListener("mousemove", handleMouseMove);
        return () => {
            window.removeEventListener("mousemove", handleMouseMove);
        };
    }, [machines, groups, sendJsonMessageThrottle]);

    const [list, setList] = useState(machines);
    const [dragging, setDragging] = useState(null);

    const handleDragStart = e => {
        e.target.addEventListener('dragend', handleDragEnd);
        setTimeout(() => {
            setDragging(e.target);
        }, 0);
    };

    const handleDragEnter = (e, group) => {
        setList(prevList => prevList.map(item => (
            item.id === parseInt(dragging.id) ? { ...item, group } : item
        )));
    };

    const handleDragEnd = e => {
        setDragging(null);
        e.target.removeEventListener('dragend', handleDragEnd);
    };

    const users = lastJsonMessage ? lastJsonMessage.users : {};
    const liveMachines = lastJsonMessage ? lastJsonMessage.machines : machines;
    const liveGroups = lastJsonMessage ? lastJsonMessage.groups : groups;

    return (
        <>
            {renderCursors(users)}
            {renderMachines(users, handleDragEnter, handleDragStart)}
        </>
    );
}

export default DragNDrop;
